                    g_zzz_yyzzzz[k] = fr * g11_zz_yyzzzz[k] + f2g * (2.0 * g20_z_yyzzzz[k] - 2.0 * fgz * g21_z_yyzzzz[k]) + 4.0 * f2t * gk_zz_yyzzz[k];

